mapper.select:
SELECT * FROM faq WHERE IS_OPEN = 'Y' AND STATUS = 'A' AND FAQ_TYPE = #{faq_type}
AND JSON_UNQUOTE(JSON_EXTRACT( TITLE, "$.${lang}" )) like #{search} '%'
ORDER BY CREATED_AT desc

SELECT *, date_format(press_date, '%Y.%m.%d') dt_date FROM content_press WHERE STATUS = 'A' AND LANG = #{langs}
AND TITLE like #{search} '%'
ORDER BY CREATED_AT desc limit #{offset} , #{size}

SELECT *, IF( date_format(end_at, '%Y.%m.%d') < date_format(now(), '%Y.%m.%d') , 'Y' , 'N') AS is_over, date_format(start_at, '%Y.%m.%d') start_date, date_format(end_at, '%Y.%m.%d') end_date FROM recruit WHERE STATUS = 'A' AND IS_OPEN = 'Y' -- AND NOW() >= START_AT -- AND NOW() <= END_AT
AND TITLE like #{search} '%'
ORDER BY SORT , CREATED_AT desc limit #{offset} , #{size}

SELECT *, date_format(created_at, '%Y.%m.%d') dt_date FROM notice WHERE STATUS = 'A' AND IS_OPEN = 'Y'
AND JSON_UNQUOTE(JSON_EXTRACT( TITLE, "$.${lang}" )) like #{search} '%'
ORDER BY CREATED_AT desc limit #{offset} , #{size}

mapper.insert:
INSERT into recruit_apply
    (
          RECRUIT_ID
        , NAME
        , PHONE_NUMBER
        , EMAIL
    )
    values
    (
          #{recruit_id}
        , #{name}
        , #{phone_number}
        , #{email}
    )
