mapper.insert:
INSERT into s3file
    (
      NAME
    , EXT
    , PATH
    , MIMETYPE
    , FILESIZE
    , URL
    , CDN_URL
     )
values
    (
      #{name}
    , #{ext}
    , #{path}
    , #{mimetype}
    , #{filesize}
    , #{url}
    , #{cdn_url}
    )

INSERT into s3file_data
    (
      GROUP_NAME
    , ROW_ID
    , S3FILE_ID
    )
values
    (
      #{table_name}
    , #{row_id}
    , #{s3file_id}
    )
