mapper.insert:
INSERT INTO CUST_APP_INFO
(
	DEVICE_ID
	, OS
	, APP_VERSION
	, LAST_ACCESS_TIME
	, REG_DT
)
VALUES
(
	#{device_id}
	, #{os}
	, #{app_version}
	, now()
	, now()
);

mapper.select:
SELECT TOKEN_NO , CUST_NO , TOKEN , EXPIRED_DT , LOGIN_DT , REG_DT FROM CUST_LOGIN_TOKEN WHERE 1=1
AND CUST_NO = #{cust_no}
AND TOKEN = #{token}
