mapper.insert:
INSERT into PAY (
	  CUST_NO
	, PAY_METHOD_CD
	, PG_YN
	, PG_CD
	, PG_TRANS_ID
	, PAYER
	, PAY_PRICE
	, NO_VAT_PRICE
	, VAT_PRICE
	, VAT
	, PAY_DT
	, REG_DT
	, PAY_WAIT_NO
	, RCPT_TYPE
	, REAL_PAYER
) values (
	 #{cust_no}
	,lower(#{pay_method})
	, 'Y'
	, #{pg_cd}
	, #{pg_trans_id}
	, #{payer}
	, #{pay_price}
	, #{no_vat_price}
	, #{vat_price}
	, #{vat}
	, now()
	, now()
	, #{pay_wait_no}
	, #{rcpt_type}
	, #{real_payer}
);

insert into CUST_PAY (
	   CUST_NO
	 , PAY_NO
	 , VALID_FROM_DT
	 , VALID_TO_DT
) values (
	   #{cust_no}
	 , #{pay_no}
	 , now()
	 , DATE_SUB((date_format(DATE_ADD(now(), INTERVAL 1 YEAR) ,'%Y-%m-%d')) , interval 1 second) /*1년 뒤 1하루 전 23:59:59초*/
	 );

insert into LOT_PAY (
	  SALE_NO
	, LOT_NO
	, PAY_NO
	, FEE_KIND_CD
) values (
	   #{sale_no}
	 , #{lot_no}
	 , #{pay_no}
	 , 'payment'
 );

mapper.select:

select L.SALE_NO, L.LOT_NO, B.BID_PRICE
from LOT L
inner join (select MAX(BID_PRICE) as bid_price from BID where SALE_NO = #{sale_no} and LOT_NO = #{lot_no}) B
where L.sale_no = #{sale_no}
and L.lot_no = #{lot_no}
and L.to_dt <= now();

mapper.insert:
INSERT INTO PAY_WAIT (CUST_NO, KIND_CD, REF_NO, REF_NO2, PAY_METHOD_CD, PG_CD, PG_TRANS_ID, PAYER, PAY_PRICE
	, STAT_CD, REG_DT, NO_VAT_PRICE, VAT_PRICE, VAT, UUID, VBANK_CD, VBANK_NM, VBANK_NUM, VBANK_EXP_DT, RCPT_TYPE
) VALUES (#{cust_no}, lower(#{kind_cd}), #{ref_no}, #{ref_no2}, lower(#{pay_method_cd}), #{pg_cd}, #{pg_trans_id}, #{payer}, #{pay_price}
	, 'wait', now(), #{no_vat_price}, #{vat_price}, #{vat}, #{uuid}, #{vbank_cd}, #{vbank_nm}, #{vbank_num}, #{vbank_exp_dt}, #{rcpt_type}
);

mapper.select:
SELECT PAY_WAIT_NO, CUST_NO, KIND_CD, REF_NO, REF_NO2, PAY_METHOD_CD, PG_CD, PG_TRANS_ID, PAYER, PAY_PRICE, STAT_CD, REG_DT
	 , NO_VAT_PRICE, VAT_PRICE, VAT, UUID, VBANK_CD, VBANK_NM, VBANK_NUM, VBANK_EXP_DT, RCPT_TYPE
FROM PAY_WAIT
WHERE pay_wait_no = #{pay_no}
  AND cust_no = #{cust_no};

SELECT CUST_NO, PAY_NO, ACADEMY_NO
FROM ACADEMY_PAY a
WHERE pay_no = #{pay_no};

select CUST_NO, LOGIN_ID, CUST_NAME, HP, EMAIL, ADDR, ADDR_DTL
from CUST
where cust_no = #{cust_no};
