mapper.insert:
INSERT into PAY (
	  CUST_NO
	, PAY_METHOD_CD
	, PG_YN
	, PG_CD
	, PG_TRANS_ID
	, PAYER
	, PAY_PRICE
	, NO_VAT_PRICE
	, VAT_PRICE
	, VAT
	, PAY_DT
	, REG_DT
) values (
	 #{cust_no}
	,#{pay_method}
	, 'Y'
	, 'nice'
	, #{pg_trnas_id}
	, #{name}
	, #{pay_price}
	, #{no_vat_price}
	, #{vat_price}
	, #{vat}
	, now()
	, now()
)

insert into CUST_PAY (
	   CUST_NO
	 , PAY_NO
	 , VALID_FROM_DT
	 , VALID_TO_DT
) values (
	   #{cust_no}
	 , ${pay_no}
	 , now()
	 , DATE_ADD(DATE_ADD(now(), INTERVAL 1 YEAR), INTERVAL -1 DAY)
	 )

insert into LOT_PAY (
	  SALE_NO
	, LOT_NO
	, PAY_NO
	, FEE_KIND_CD
) values (
	   #{sale_no}
	 , #{lot_no}
	 , last_insert_id()
	 , 'payment'
 )

INSERT INTO ACADEMY_PAY (
		ACADEMY_NO,
		CUST_NO,
		PAY_NO
	)
	VALUES (
	    #{academy_no},
	    #{cust_no},
	    ${pay_no}
	)
